import { mergeProps, splitProps } from "solid-js";
import classNames from "./classnames";
import { useBootstrapPrefix } from "./ThemeProvider";
import AbstractModalHeader from "./AbstractModalHeader";
const defaultProps = {
    closeLabel: "Close",
    closeButton: false,
};
const OffcanvasHeader = (p) => {
    const [local, props] = splitProps(mergeProps(defaultProps, p), [
        "bsPrefix",
        "class",
        "className"
    ]);
    const bsPrefix = useBootstrapPrefix(local.bsPrefix, "offcanvas-header");
    return (<AbstractModalHeader {...props} class={classNames(local.class, local.className, bsPrefix)}/>);
};
export default OffcanvasHeader;
